import React, { useState } from 'react'
import AContainer from './AwardContainer';
import 'aos/dist/aos.css'

const UniAward = () => {

    return(   
            <div className='max-w-[1240px] mx-auto grid md:grid-cols-4 lg:grid-cols-4 sm:grid-cols2 gap-8 my-4 '>
                <AContainer title = "National Math Scholarship" description= "(Value: $15,000) awarded by the Faculty of Mathematics at the University of Waterloo"
                details="for having a distinguished academic standing, as well as a record of accomplishments in mathematics and/or computer science contests."/>
                <AContainer title = "President's Scholarship of Distinction" description = "($2,000 Value) awarded by the University of Waterloo" 
                details="For having an early May admission average of 95%+"/>
            </div>
    )

}

const MathAward = () => {

    return(   
            <div className='max-w-[1240px] mx-auto grid md:grid-cols-3 lg:grid-cols-4 sm:grid-cols2 gap-5 my-4'>
                <AContainer title = 'Canadian Senior Math Roll Group V' description="awarded by the university of Waterloo"/>
                <AContainer title = "Hypatia Honor Roll Group III" description="awarded by the university of Waterloo"/>
                <AContainer title = 'Cayley Honor Roll Group IV' description="awarded by the university of Waterloo"/>
                <AContainer title = 'Cayley Honor Roll Group IV' description="awarded by the university of Waterloo"/>
            </div>
    )

}

const MiscAward = () => {

    return(   
            <div className='max-w-[1240px] mx-auto grid md:grid-cols-4 lg:grid-cols-4 sm:grid-cols2 gap-8 my-4'>
                <AContainer title = 'Summa Cum Laude' description="awarded by Westdale Secondary School for being in the top 5% of the graduating class"/>
                <AContainer title = 'Honor Roll' description="awarded by Westdale Secondary School for having an 80%+ average from 2019-2023"/>
                <AContainer title = 'DELF B1' description="awarded by France Education International for having a B1 proficiency in French"/>
                <AContainer title = 'RCM Level 8' description="awarded by the Royal Conservatory of Music for Piano"/>
            </div>
    )

}


const Awards = () => {
    const [selected, setSelected] = useState('Uni');

    const handleChange=(e)=>{
        setSelected(e.target.value)
    }
    
    return(
        <section id='Awards' > 
            <div data-aos='slide-up' data-aos-duration = {1000}>
                <h1 className='font-bold md:text-4xl sm:text-3xl text-2xl py-4 flex justify-center dark:text-white'>Awards</h1>
                <div className='flex justify-center mb-8'>
                <select value={selected} onChange={(e)=>handleChange(e)} className='scale-125 font-bold text-center rounded-lg outline-none bg-[#c288fc] dark:bg-[#fcdf88] text-white dark:text-black'>
                    <option>All</option>
                    <option>Uni</option>
                    <option>Math</option>
                    <option>Misc</option>
                    <option>None</option>
                </select> 
                </div>
                {selected === "All" ? <div className=''> <UniAward/> <MathAward/> <MiscAward/> </div>: "" }
                {selected === "Uni" ? <UniAward/> : "" }
                {selected === "Math" ? <MathAward/>:"" }
                {selected === "Misc" ? <MiscAward/> :"" }
                {selected === "None" ? "" :"" }
            </div>
        </section>
    )

}

export default Awards
